class CountPattern {
  String text =
      '';
  String pattern = 'CGCGCAGCG';
  int totalCount = 0;

  void run() {
    for (int i = 0; i <= text.length - pattern.length; i++) {
      var substr = text.substring(i, i + pattern.length);
      print('Sub: $substr');
      if (substr == pattern) {
        totalCount++;
      }
    }

    print('Total Count: $totalCount');
  }
}

class FrequentPatterns {
  String text =
      '';
  int patternSize = 11;
  int totalCount = 0;

  void run() {
    var pattern = Map<String, int>();
    for (var i = 0; i <= text.length - patternSize; i++) {
      var substr = text.substring(i, i + patternSize);
      if (pattern[substr] == null) {
        pattern[substr] = 1;
      } else {
        pattern[substr] = pattern[substr]! + 1;
      }
    }

    int max = 0;
    List<String> maxPattern = [];
    pattern.forEach((key, value) {
      if (value > max) {
        maxPattern.clear();
        maxPattern.add(key);
        max = value;
      } else if (value == max) {
        maxPattern.add(key);
      }
    });

    print('All these patterns have appear the max of $max times:');
    print('$maxPattern');
  }
}

class GetReverseComplement {
  String dna =
      '';

  Map<String, String> rules = {
    'A': 'T',
    'T': 'A',
    'C': 'G',
    'G': 'C',
  };

  void run() {
    String complement = '';

    // Pega o complementar e inverte a sequência
    for (var i = dna.length - 1; i >= 0; i--) {
      var nucleotide = dna[i];
      complement = '$complement${rules[nucleotide]}';
    }

    print('Molde: $dna');
    print('Complementar: $complement');
  }
}

class OutputPatternStartingPosition {
  String dna =
      '';
  String pattern = 'TAGAACCTA';

  void run() {
    List<int> startingPositions = [];
    for (var i = 0; i <= dna.length - pattern.length; i++) {
      var substr = dna.substring(i, i + pattern.length);
      if (substr == pattern) {
        startingPositions.add(i);
      }
    }
    print('Posições iniciais do padrão: $startingPositions');
  }
}

class OutputPatternStartingPositionFromVibrio {
  var pattern = 'CTTGATCAT';

  void run() {
    File loadVibrio = File.fromUri(Uri.file('Vibrio_cholerae.txt'));
    var dna = loadVibrio.readAsStringSync();

    List<int> startingPositions = [];
    for (var i = 0; i <= dna.length - pattern.length; i++) {
      var substr = dna.substring(i, i + pattern.length);
      if (substr == pattern) {
        startingPositions.add(i);
      }
    }
    print('Posições iniciais do padrão: $startingPositions');
  }
}
